import { render, screen } from '@testing-library/react';

import { Image } from '.';

describe('Image functionality', () => {
  test('Placeholder should be visible as background image', () => {
    render(<Image src="/images/handyBillDark.svg" alt="test" layout="fill" />);

    expect(screen.getByRole('img').style.backgroundImage).toBe(
      'url(data:image/svg+xml;base64,)',
    );
  });
});
